function loadImg(){
 var imgrs = document.querySelectorAll('img.lazyload');
 if (imgrs !== null) {
  for (var i = 0; i < imgrs.length; ++i) {
   var img = imgrs[i],
   n = img.getAttribute('data-src');
   if(n.indexOf('bp.blogspot') > 0){
    var w = img.offsetWidth.toFixed(0),
    ow = img.getAttribute('width'),
    mw = img.getAttribute('data-original-width'),
    pw = img.parentElement.offsetWidth.toFixed(0),
    h = img.offsetHeight.toFixed(0),
    p = n.split( '/' ),
    r = '';
    if(hasClass(img.parentElement, 'ratio')){
     r = 'w'+w+'-h'+h+'-c';
     }else{
     nw = ow ? ow : mw ? mw : w;
     r = pw <= parseFloat(nw) ? 'w'+pw : 'w'+nw;
    }
    n = n.replace( p[ p.length - 2 ], r);
    img.setAttribute('data-src',n);
   }
  }
 }
