const pagination = {};

function loadPagination(e){
	var show = e.getAttribute('data-pagination');
	if(show != 'false'){
		
		var next_text = '<svg aria-hidden="true" height="1em" style="vertical-align:-.1em" width="1em"><use xlink:href="#i-arrow-l"/></svg>';
		var prev_text = '<svg aria-hidden="true" height="1em" style="vertical-align:-.1em" width="1em"><use xlink:href="#i-arrow-r"/></svg>';
		var posts = e.getAttribute('data-posts');
		var label = e.getAttribute('data-label');
		
		label = encodeURIComponent(label);
		var urilabel = label ? '-/'+label+'/' : '';
		var urllabel = label ? '/label/'+label : '';
		Defer.js(site_url+'feeds/posts/summary/'+urilabel+'?alt=json&callback=pagination.key&max-results=1');
		
		var max_post = getParam('max-results',current_url);
		var page = getParam('page',current_url);
		var page_size = max_post ? max_post : posts;
		var paged = page ? page : 1;
		
		pagination['key'] = function(data) {
			var feed = data.feed,
			total = parseInt(feed.openSearch$totalResults.$t);
			if(total > posts){
				var data = paginate(total, paged, page_size, 5);
				var ul = document.createElement('ul');
				var lf = 1;
				var ls = data.totalPages;
				
				if(data.currentPage != lf){
					var next = list_generate(data.currentPage - 1 , '', next_text, 'arrow');
					ul.appendChild(next);
				}
				if(!inArray(data.pages, lf)){
					var li = list_generate(lf , data.currentPage, lf+' . .');
					ul.appendChild(li);
				}
				
				data.pages.forEach(function(v, k) {
					var li = list_generate(v , data.currentPage);
					ul.appendChild(li);
				});
				
				if(!inArray(data.pages, ls)){
					var li = list_generate(ls , data.currentPage, '. . '+ls);
					ul.appendChild(li);
				}
				
				if(data.currentPage != ls){
					var prev = list_generate(data.currentPage + 1 , '', prev_text, 'arrow');
					ul.appendChild(prev);
				}
				
				e.innerHTML = '';
				addClass(ul , 'pagination mb-0');
				e.appendChild(ul);
				removeClass(e, 'invisible');
			}
		}
		
		function list_generate(l , v , t, c){
			var li = document.createElement('li');
			var btn = document.createElement('span');
			addClass(btn, 'btn btn-sm rounded-pill');
			btn.innerHTML = t ? t : l;
			btn.setAttribute('data-page', l);
			if(l == v){
				addClass(btn, 'btn-color');
				}else{
				c&&addClass(btn , c);
				addClass(btn , 'btn-light hover-btn-color');
				btn.addEventListener('click',function(e){
					e.preventDefault();
					paged = btn.getAttribute('data-page');
					if (paged == 1) {
						var redirect = !label? site_url : site_url+'search'+urllabel+'?max-results='+posts+'&page='+paged; 
						window.location.href = redirect;
						}else{
						var start = (paged - 1) * posts;
						Defer.js(site_url+'feeds/posts/summary/'+urilabel+'?start-index='+start+'&alt=json&callback=pagination.date&max-results=1');
					}  
				}); 
			}
			li.appendChild(btn);
			return li;
		};
		
		pagination['date'] = function(data) {
			var post = data.feed.entry[0];
			var date = post.published.$t.substring(0, 19) + post.published.$t.substring(23, 29);
			date = date.replace('+', '%2B');
			var redirect = site_url+'search'+urllabel+'?updated-max='+date+'&max-results='+page_size+'&page='+paged;
			window.location.href = redirect;
		}
		
		}else{
		removeClass(e, 'invisible');
	}
}

Defer.dom('#pagination',100,'jetpaged',function(e) {
	loadPagination(e);
});

const custom_posts = {};

function loadCustomPosts(e) {

    var t = token();
    var limit = e.getAttribute('data-items');
    var no_item = e.getAttribute('data-no-item');
    var func_name = e.getAttribute('data-func');
    var max_items = no_item ? (parseInt(limit) + 1) : limit;
    var argr_month = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    var labels = e.innerHTML;

    var args = labels.split(',');
    var query = '';

    if (args.length > 1) {
        query = labels ? '-/' + encodeURIComponent(args[Math.floor(Math.random() * args.length)]) + '/?' : '?';
    } else {
        query = labels ? '-/' + encodeURIComponent(labels.trim()) + '/?' : '?';
    }

    Defer.js(site_url + 'feeds/posts/summary/' + query + 'alt=json&callback=custom_posts.key_' + t + '&max-results=' + max_items);

    custom_posts['key_' + t] = function(data) {

        var total = parseInt(data.feed.openSearch$totalResults.$t);

        if (total > 0) {

            var results = [];

            var posts = data.feed.entry;

            var c = 0;

            for (var i = 0; i < posts.length; ++i) {

                var post = posts[i];
                var link = post.link[post.link.length - 1].href;
                if (c == limit) {
                    break;
                }
                if (link != no_item) {
                    c++;

                    var result = {};

                    result['grup_id'] = t;
                    result['url'] = link;
                    result['title'] = post.title.$t;
                    result['summary'] = post.summary.$t;
                    result['img'] = post.media$thumbnail && post.media$thumbnail.url;
                    result['author'] = post.author[0].name.$t;
                    result['comment'] = post.thr$total && post.thr$total.$t;
					result['label'] = post.category;

                    var publish = post.updated.$t;
                    var date = new Date(publish);
                    var day = date.getDate();
                    var month = argr_month[date.getMonth()];
                    var year = date.getFullYear();

                    result['date'] = day + ' ' + month + ', ' + year;
                    

                    results.push(result);

                }
            }
            var func = window[func_name];
            if (typeof func === 'function' && results.length > 0) {
                e.innerHTML = func(results).trim();
                removeClass(e, 'visually-hidden');
                loadImg();
            }

        }
    }
}

Defer.dom('.custom-posts',100,'jetfeeds',function(e) {
  loadCustomPosts(e);
});

